(3, 'Breaking Down the Journey to a Winning TOP University Application', 'breaking-down-the-journey-to-a-winning-top-university-application', '2023-07-17', '14:00:00', 'parents', 'https://crm-allinedu.com/form/event?event_name=test', '<p>Discover the essential elements to maximize your chances of acceptance and unlock opportunities for a successful academic journey!</p>', '<p>Discover the essential elements to maximize your chances of acceptance and unlock opportunities for a successful academic journey!</p>\r\n<ul>\r\n<li>Lorem ipsum 1</li>\r\n<li>Lorem ipsum 2</li>\r\n<li>Lorem ipsum 3</li>\r\n<li>Lorem ipsum 4&nbsp;</li>\r\n</ul>', '[\"Speaker-image-20230810112345-1.webp\",\"Speaker-image-20230810112345-2.webp\"]', 'active', '2023-08-10 04:15:36', '2023-08-10 04:23:45'),
(4, 'Topic Lama', 'topic-lama', '2023-07-09', '12:00:00', 'both', 'https://crm-allinedu.com/form/event?event_name=test', '<p>Test</p>', '<p>&nbsp;Lorem ipsumm test</p>\r\n<p>&nbsp;<img src=\"data:image/png;base64,\"></p>', '[\"Speaker-image-20230810111351-1.webp\",\"Speaker-image-20230810111351-2.webp\"]', 'active', '2023-08-10 04:15:07', '2023-08-10 04:15:07'),
(5, 'A PARENT\'S ULTIMATE GUIDE TO TOP UNIVERSITY APPLICATION EXCELLENCE', 'a-parents-ultimate-guide-to-top-university-application-excellence', '2023-07-21', '10:00:00', 'students', 'https://crm-allinedu.com/form/event?event_name=test', '<p>Top university application requirements and deadlines break down.<br>Expert guidance for creating an optimal application timeline.&nbsp;<br>Personalized action plans for each grade level.<br>Inspiring success stories of parents supporting their children\'s top university admissions.</p>', '<p>Discover the essential elements to maximize your chances of acceptance and unlock opportunities for a successful academic journey!</p>\r\n<p>Lorem ipsum 1<br>Lorem ipsum 2<br>Lorem ipsum 3<br>Lorem ipsum 4&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Test</p>', '[\"Speaker-image-20230710141409-1.png\",\"Speaker-image-20230710141409-2.png\"]', 'active', '2023-07-17 07:09:33', '2023-08-10 04:32:03');
